def compute_Last_Digit(a, b):
    if a == 0:
        return 1
    elif b == 0 or a > b:
        return 0

    factorial_a = 1
    for i in range(1, a + 1):
        factorial_a *= i

    factorial_b = 1
    for i in range(1, b + 1):
        factorial_b *= i

    if factorial_b % factorial_a == 0:
        return factorial_a % 10
    else:
        return -1

