    eulerian = [[0 for _ in range(n + 1)] for _ in range(n + 1)]

    for i in range(n + 1):
        eulerian[i][0] = 1 if i == 0 else 0

    for i in range(1, n + 1):
        for j in range(1, i + 1):
            eulerian[i][j] = (i - j + 1) * eulerian[i - 1][j] + (j + 1) * eulerian[i - 1][j - 1]

    return eulerian[n][m]

