def modular_inverse(arr, p, n):
    def mod_inverse(x, p):
        # Extended Euclidean Algorithm to find modular inverse
        original_p = p
        y, prev_y = 0, 1
        while x > 1:
            q = x // p
            p, x = x % p, p
            y, prev_y = prev_y - q * y, y
        return prev_y + original_p if prev_y < 0 else prev_y

    count = 0
    for num in arr:
        if num != 0 and mod_inverse(num, p) % p == num % p:
            count += 1

    return count

