def is_Diff(num):
    even_sum = 0
    odd_sum = 0

    for digit in str(abs(num)):
        if int(digit) % 2 == 0:
            even_sum += int(digit)
        else:
            odd_sum += int(digit)

    return even_sum - odd_sum

