def breakSum(n):
    if n <= 1:
        return n
    max_sum = 0
    for i in range(1, n):
        for j in range(1, n - i + 1):
            max_sum = max(max_sum, breakSum(i) + breakSum(j) + breakSum(n - i - j))
    return max_sum

