def ncr_modp(n, r, p):
    if r > n:
        return 0
    if r == 0 or r == n:
        return 1

    numerator = 1
    denominator = 1

    for i in range(r):
        numerator = (numerator * (n - i)) % p
        denominator = (denominator * (i + 1)) % p

    def mod_inverse(a, p):
        return pow(a, p - 2, p)

    denominator_inv = mod_inverse(denominator, p)

    return (numerator * denominator_inv) % p

