def max_difference(tuple_list):
    if not tuple_list:
        return 0

    min_val = float('inf')
    max_val = float('-inf')

    for a, b in tuple_list:
        min_val = min(min_val, a, b)
        max_val = max(max_val, a, b)

    return max_val - min_val

