    import re

    upper_case = len(re.findall(r'[A-Z]', str))
    lower_case = len(re.findall(r'[a-z]', str))
    numbers = len(re.findall(r'[0-9]', str))
    special_chars = len(re.findall(r'[^A-Za-z0-9]', str))

    return (upper_case, lower_case, numbers, special_chars)

