        return []

    encoding = []
    count = 1

    for i in range(1, len(lst)):
        if lst[i] == lst[i - 1]:
            count += 1
        else:
            if count > 1:
                encoding.append([count, lst[i - 1]])
            else:
                encoding.append(lst[i - 1])
            count = 1

    if count > 1:
        encoding.append([count, lst[-1]])
    else:
        encoding.append(lst[-1])

