def find_Product(arr, n):
    from collections import Counter
    counter = Counter(arr)
    product = 1
    has_non_repeated = False

    for num, count in counter.items():
        if count == 1:
            product *= num
            has_non_repeated = True

    return product if has_non_repeated else 0

